import React from "react";
import Form from "./Form";
import Plan from "./Plan";
import axios from 'axios'

class Main extends React.Component
{
    constructor(props) {
        super(props);
        this.state = {
            monday: [],
            tuesday: [],
            wednesday: [],
            thursday: [],
            friday: [],
            saturday: [],
            sunday: []
        };
        this.updateList = this.updateList.bind(this);
    }

    async updateList() {
        let tasks;
        axios.get("http://localhost:5000/get-tasks")
            .then((res) => { tasks = res.data });
        console.log("hi");
        return tasks;
    }

    render() {
        return (<main>
           <Form update={this.updateList} tasks={this.updateList()} />
           <Plan update={this.updateList} tasks={this.updateList()} />
        </main>);
    }
}

export default Main;
