import React from "react";
import Form from "./Form";
import Plan from "./Plan";
import axios from 'axios'

class Main extends React.Component
{
    constructor(props) {
        super(props);
        this.state = {
            monday: [],
            tuesday: [],
            wednesday: [],
            thursday: [],
            friday: [],
            saturday: [],
            sunday: []
        };
        this.updateList = this.updateList.bind(this);
    }

    componentDidMount() {
        this.updateList()
            .then(res => console.log(res));
    }

    async updateList() {
        let tasks;
        this.setState({
            monday: [],
            tuesday: [],
            wednesday: [],
            thursday: [],
            friday: [],
            saturday: [],
            sunday: []
        });
        await axios.get("http://localhost:5000/get-tasks")
            .then((res) => { tasks = res.data })
            .then(() => {
                tasks.map((item) => {
                    switch(item.day)
                    {
                        case 'Monday':
                            this.setState({
                                monday: [...this.state.monday, item]
                            });
                            break;
                        case "Tuesday":
                            this.setState({
                                tuesday: [...this.state.tuesday, item]
                            });
                            break;
                        case 'Wednesday':
                            this.setState({
                                wednesday: [...this.state.wednesday, item]
                            });
                            break;
                        case 'Thursday':
                            this.setState({
                                thursday: [...this.state.thursday, item]
                            });
                            break;
                        case 'Friday':
                            this.setState({
                                friday: [...this.state.friday, item]
                            });
                            break;
                        case 'Saturday':
                            this.setState({
                                saturday: [...this.state.saturday, item]
                            });
                            break;
                        case 'Sunday':
                            this.setState({
                                sunday: [...this.state.sunday, item]
                            });
                            break;
                        default:
                            break;
                    }
                });
            });
    }

    render() {
        return (<main>
           <Form update={this.updateList} tasks={this.state} />
           <Plan update={this.updateList} tasks={this.state} />
        </main>);
    }
}

export default Main;
