import React from 'react'
import axios from 'axios'

class Form extends React.Component
{
    constructor(props) {
        super(props);
        this.state = {
          exercise: "",
          duration: 0
        };
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        this.setState({
            [e.target.name]: e.target.value
        });
    }

    handleSubmit(e) {
        e.preventDefault();
        let taskSend = this.state.task;
        let durationSend = this.state.duration;
        try
        {
            axios.post("http://localhost:5000/add",{
                task: taskSend,
                duration: durationSend
            })
                .then(() => { console.log("Dodano") });
        }
        catch(e) {
            console.log("Error " + e);
        }
    }

    render() {
        return (<div className="form">
            <h2>Add task</h2>
            <form>
                <input type="text" name="exercise" placeholder="Task" onChange={e => this.handleChange(e)} />
                <input type="number" name="duration" placeholder="Duration" onChange={e => this.handleChange(e)} />
                <button onClick={e => this.handleSubmit(e)}>Add</button>
            </form>
        </div>);
    }
}

export default Form;
