import React from 'react'
import axios from 'axios'

class Form extends React.Component
{
    constructor(props) {
        super(props);
        this.state = {
          exercise: "",
          duration: 0
        };
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        this.setState({
            [e.target.name]: e.target.value
        });
    }

    handleSubmit(e) {
        e.preventDefault();
        axios.post("http://localhost:5000/add",{
            task: this.state.task,
            duration: this.state.duration
        })
            .then(() => { console.log("Dodano") });
    }

    render() {
        return (<div className="form">
            <h2>Add task</h2>
            <form>
                <input type="text" name="exercise" placeholder="Task" onChange={e => this.handleChange(e)} />
                <input type="number" name="duration" placeholder="Duration" onChange={e => this.handleChange(e)} />
                <button onClick={e => this.handleSubmit(e)}>Add</button>
            </form>
        </div>);
    }
}

export default Form;
